package billingSDK.billingDemo;import java.io.BufferedReader;import java.io.InputStreamReader;import org.json.JSONArray;import org.json.JSONException;import org.json.JSONObject;import org.json.JSONTokener;import android.app.Activity;import android.util.Log;import android.widget.Toast;public class SmsPayCodes{	private String _payCodesJson;		private SmsPayCodes() {}		private SmsPayCodes(Activity activity) {				try {			InputStreamReader inputReader = new InputStreamReader(activity.getResources().getAssets().open("PayCodes.json") );			BufferedReader bufReader = new BufferedReader(inputReader);			String line="";			String result="";			while((line = bufReader.readLine()) != null)				result += line;			_payCodesJson = result;			Log.e("========PayCodesJson========", _payCodesJson);		} catch (Exception e) {	       e.printStackTrace();	    }	}	private static SmsPayCodes _singletonSmsPayCodes;		public static SmsPayCodes getInstance(Activity context) {		if (_singletonSmsPayCodes == null) {			_singletonSmsPayCodes = new SmsPayCodes(context);		}		return _singletonSmsPayCodes;	}		private String[] getCodeStringsWithKey(String key) {		try {			JSONTokener jsonParser = new JSONTokener(_payCodesJson);			JSONObject person = (JSONObject) jsonParser.nextValue();			JSONArray codes = person.getJSONArray(key);						String[] result = new String[codes.length()];			for (int i = 0; i < result.length; i++) {				result[i] = codes.getString(i);			}			return result;		} catch (JSONException e) {			e.printStackTrace();		}		return null;	}		public String[] getCMCC_GC_PayCodes() {		return getCodeStringsWithKey("CMCC_GC");	}		public String[] getUNICOM_PayCodes() {		return getCodeStringsWithKey("UNICOM");	}		public String[] getCMCC_MM_PayCodes() {		return getCodeStringsWithKey("CMCC_MM");	}		public String[] getTELECOM_CTE_PayCodes() {		return getCodeStringsWithKey("TELECOM_CTE");	}}